import { useState, useEffect } from "react"
import { registerClickAway } from '../helpers/click-away'
import NavItem from './NavItem'
import ProductsPanel from './ProductsPanel'
import LearningPanel from './LearningPanel'
import NavMenu from './NavMenu'
import MobileNavMenu from './MobileNavMenu'

export default function NavBar () {
  const [open, setOpen] = useState()
  const [mobileMenuOpen, setMobileMenuOpen] = useState()

  function showMenu(menu) {
    if (open === menu) return setOpen(null)

    setTimeout(() => {
      setOpen(menu)
    }, 0)
  }

  useEffect(() => {
    if (open) registerClickAway(() => {
      setOpen(null)
    })
  }, [open])

  const communityMenuItems = [
    { text: 'Tools & Services', href: '/docs/tooling', description: 'Explore the tools and services our awesome community has created.' },
    { text: 'Github Organization', href: 'https://github.com/asyncapi', target: '_blank', description: 'Want to sneak in the code? Everything we do is open-sourced in our Github organization.' },
    { text: 'Slack Workspace', href: 'https://asyncapi.com/slack-invite', target: '_blank', description: `Need help? Want to share something? Join our Slack workspace. We're nice people :)` },
  ]

  return (
    <div className="relative bg-white">
      <div className="flex justify-between items-center py-6 md:justify-start md:space-x-10">
        <div className="lg:w-0 lg:flex-1">
          <a href="/" className="flex">
            <img className="h-8 w-auto sm:h-8" src="/img/logos/asyncapi-horizontal-color.svg" alt="AsyncAPI" />
          </a>
        </div>
        <div className="-mr-2 -my-2 md:hidden">
          <button onClick={() => setMobileMenuOpen(true)} type="button" className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:bg-gray-100 focus:text-gray-500 transition duration-150 ease-in-out">
            <svg className="h-6 w-6" stroke="currentColor" fill="none" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16"/>
            </svg>
          </button>
        </div>
        <nav className="hidden md:flex space-x-10">
          <div className="relative">
            <NavItem text="Learning" onClick={() => showMenu('learning')} hasDropdown />
            {open === 'learning' && <LearningPanel />}
          </div>

          <div className="relative">
            <NavItem text="Tools" onClick={() => showMenu('tooling')} hasDropdown />
            { open === 'tooling' && <ProductsPanel /> }
          </div>

          <div className="relative">
            <NavItem text="Community" onClick={() => showMenu('community')} hasDropdown />
            {open === 'community' && <NavMenu items={communityMenuItems} />}
          </div>

          <NavItem href="/blog" text="Blog" />
          <NavItem href="https://asyncapi.threadless.com" text="Shop" target="_blank" />
        </nav>
        <div className="hidden md:flex md:flex-1">

        </div>
      </div>

      { /* Mobile menu, show/hide based on mobile menu state. */ }
      { mobileMenuOpen && <MobileNavMenu onClickClose={() => setMobileMenuOpen(false)} /> }
    </div>
  )
}
