export function registerClickAway(callback) {
  document.removeEventListener("click", callUnregister)
  document.addEventListener("click", callUnregister)

  document.querySelectorAll('iframe').forEach(iframe => {
    if (iframe.attributes.src.value.startsWith('/') && !iframe.attributes.src.value.startsWith('//')) {
      iframe.contentWindow.document.removeEventListener("click", callUnregister)
      iframe.contentWindow.document.addEventListener("click", callUnregister)
    }
  })

  function callUnregister (...args) {
    unregisterClickAway(...args)
    callback()
  }
}

export function unregisterClickAway() {
  document.removeEventListener("click", unregisterClickAway)
  document.querySelectorAll('iframe').forEach(iframe => {
    if (iframe.attributes.src.value.startsWith('/') && !iframe.attributes.src.value.startsWith('//')) {
      iframe.contentWindow.document.removeEventListener("click", unregisterClickAway)
    }
  })
}
